import React from "react";
import userContext from "../context/users/userContext";
import { useContext } from "react";
const Contacts = (props)=>{
    const User=useContext(userContext);
    const handleOnClick = async ()=>{
        await User.updateCurrentUser(props.contactName,props.recipients,false);
        // console.log("Contacts::",props.contactName);
    }
    return (
        <div>
            <div className="contact-list">
                <div className="contacts" onClick={handleOnClick}>
                    <h5 className="contact-name" >contactName:{props.contactName}</h5>
                    <h5 className="contact-id" > <br></br> username: {props.recipients[0]}</h5>
                </div>
            </div>
        </div>
    )
}

export default Contacts;
