import { useEffect, useState } from "react";
import userContext from "./userContext";
import useLocalStorage from "../../hooks/useLocalStorage";

const UserState =(props)=>{
    const [currentUser,setcurrentUser] = useState({
        "username":"User2",
        "id":'2'
    })

    const updateCurrentUser = (newUser, newId) => {
        setcurrentUser({
          username: newUser,
          id: newId
        });
      };
      
    const [Message,setMessage] = useState([])
    const updateMessage = (newMessage) => {
        setMessage((prevMessage) => [
          ...prevMessage,
          {
            text: newMessage,
          },
        ]);
      };
      const [myContacts,setmyContacts] = useState([
          // {
          //     "name":'',
          //     "id":''
          // }
      ])
     useEffect(()=>{
        myContacts.forEach((contact)=>{
          if(contact.id!==currentUser.id) return;
          setMessage(
            contact.message,

          )
        })
        console.log(myContacts);
     },[myContacts,currentUser.id])
    // const [myId,setmyId] = useLocalStorage('id');
    // const updatemyId =(newId)=>{
    //     setmyId(newId);
    // }
    const updatemyContacts=(newContact)=>{
        setmyContacts([...myContacts,{
            "name":newContact.name,
            "id":newContact.id,
            message:[]
        }])
    }

    const addMessageToContact = (newMessage) => {
      setmyContacts((prevContacts) =>
        prevContacts.map((contact) => {
          if (contact.id === newMessage.id) {
            return {
              ...contact,
              message: [...contact.message, { text: newMessage.text }],
            };
          }
          return contact;
        })
      );
    };
    
    return(
        <userContext.Provider value={{myContacts,updatemyContacts,currentUser,updateCurrentUser,Message,updateMessage,addMessageToContact}}>
            {props.children}
        </userContext.Provider>
    )
}
export default UserState ;
