import React from 'react';
import Chatbox from './Chatbox';
import Message from './Message';
import userIcon from '../img/user.png';
import moreIcon from '../img/More-icon.png';
import { useContext } from 'react';
import userContext from '../context/users/userContext';

const Chatpane = () => {
  const User = useContext(userContext);

  return (
    <div className="chat-pane">
      <div className="chat-topbar">
        <img className="userIcon" src={userIcon} alt="User" />
        <span className="Group-name">{User.currentUser.username}</span>
        <img className="moreIcon" src={moreIcon} alt="More" />
      </div>
      <div className="chats">
        <Message
          name="User"
          text="lorem njkdj kc kcejdwskdk j n kcxjnekjbjxj edj dbn xjd jdnb dn ndjnj djn kdx kjks jnsk hjsk jnsk js kshj nksnn."
        />
        <div className="outgoing-chats">
          {User.myContacts.map((contact) => {
            if (contact.id !== User.currentUser.id) return null;
            return contact.message.map((message, index) => (
              <div className="mychat" key={index}>
                <Message name={props.myId} text={message.text} />
              </div>
            ));
          })}
        </div>
      </div>
      <div className="bottom-bar">
        <Chatbox />
      </div>
    </div>
  );
};

export default Chatpane;
