import React from 'react';
import sendIcon from '../img/send.png';
import { useContext ,useState} from 'react';
import userContext from '../context/message/userContext';
// import userContext from "../context/users/userContext";
const Chatbox = () => {
  // const message = useContext(currentMessageContext);
  const User=useContext(userContext);

  const [messageTyped,setmessageTyped] = useState("");
  
  const handleOnChange=(event)=>{
    setmessageTyped(event.target.value);
    console.log(messageTyped);
  }
  const onSendingMessage=(event)=>{
    // const User=useContext(userContext);
    User.updateMessage({messageTyped});
    console.log(User.Message.text);
  }
  return (
    <div>
      
        <form className="chatbox" >
          <input type="text" placeholder='Type here' onChange={handleOnChange}/>
          <button type='submit' className="send-icon" onSubmit={onSendingMessage}>
            <img src={sendIcon} alt="Send" />
          </button>
        </form>
      
    </div>
  );
}

export default Chatbox;
