import React from 'react';
import sendIcon from '../img/send.png';
import { useContext ,useState} from 'react';
import currentMessageContext from '../context/message/currentMessageContext';
// import userContext from "../context/users/userContext";
const Chatbox = () => {
  const message = useContext(currentMessageContext);
  const messageTyped = useState("");
  
  const onSendingMessage=(event)=>{
    // const User=useContext(userContext);
    message.updateMessage({messageTyped});
    console.log(currentMessageContext.Message.text)
  }
  return (
    <div>
      
        <form className="chatbox" >
          <input type="text" placeholder='Type here' value={messageTyped}/>
          <button type='submit' className="send-icon" onSubmit={onSendingMessage}>
            <img src={sendIcon} alt="Send" />
          </button>
        </form>
      
    </div>
  );
}

export default Chatbox;
