import { useEffect, useState } from "react";
import userContext from "./userContext";
import useLocalStorage from "../../hooks/useLocalStorage";

const UserState =(props)=>{
    const [currentUser,setcurrentUser] = useState({
        "username":"User2",
        "id":'2'
    })

    const updateCurrentUser = (newUser, newId) => {
        setcurrentUser({
          username: newUser,
          id: newId
        });
      };
      
    const [Message,setMessage] = useState([])
    const updateMessage = (newMessage) => {
        setMessage((prevMessage) => [
          ...prevMessage,
          {
            text: newMessage,
          },
        ]);
      };
      const [myContacts,setmyContacts] =useLocalStorage('ic',[]);
      //   useEffect(() => {
      //     myContacts.forEach((contact) => {
      //       if (contact.id !== currentUser.id) return;
      //       setMessage((prevMessage) => [...prevMessage, ...contact.message]);
      //     });
      //     console.log(myContacts);
      // }, [myContacts, currentUser.id]);
      
      // const [myId,setmyId] = useLocalStorage('id');
      // const updatemyId =(newId)=>{
        //     setmyId(newId);
        // }
        const updatemyContacts = (newContact) => {
          setmyContacts((prevContacts) => [
            ...prevContacts,
            {
              name: newContact.name,
              id: newContact.id,
              message: [],
            },
          ]);
        };
        
        const addMessageToContact = (newMessage) => {
          setmyContacts(
          myContacts.map((contact) => {
            if (contact.id === newMessage.id[0]) {
              return {
                'name': contact.name,
                'id': contact.id,
                'message': [...contact.message, newMessage.text],
              };
            }else
            return contact;
          })
          );
    };
    
    return(
        <userContext.Provider value={{myContacts,updatemyContacts,currentUser,updateCurrentUser,Message,updateMessage,addMessageToContact}}>
            {props.children}
        </userContext.Provider>
    )
}
export default UserState ;
