import { useState } from "react";
import userContext from "./userContext";
import useLocalStorage from "../../hooks/useLocalStorage";

const UserState =(props)=>{
    const [currentUser,setcurrentUser] = useState({
        "username":"User2",
        "id":'2'
    })

    const updateCurrentUser = (newUser, newId, callback) => {
        setcurrentUser({
          "username": newUser,
          "id": newId
        }, () => {
          if (callback) {
            callback();
          }
        });
      };
      
    const [Message,setMessage] = useState([{
        "text": "",
    }])
    const updateMessage = (newMessage) => {
        setMessage((prevMessage) => [
          ...prevMessage,
          {
            text: newMessage,
          },
        ]);
      };
    
    // const [myId,setmyId] = useLocalStorage('id');
    // const updatemyId =(newId)=>{
    //     setmyId(newId);
    // }
    const [myContacts,setmyContacts] = useState([
        {
            "name":'',
            "id":''
        }
    ])
    const updatemyContacts=(newContact)=>{
        setmyContacts([...myContacts,{
            "name":newContact.name,
            "id":newContact.id
        }])
    }
    return(
        <userContext.Provider value={{myContacts,updatemyContacts,currentUser,updateCurrentUser,Message,updateMessage}}>
            {props.children}
        </userContext.Provider>
    )
}
export default UserState ;
