import React from 'react';
import sendIcon from '../img/send.png';
import { useContext } from 'react';
import currentMessageContext from '../context/message/currentMessageContext';

const Chatbox = () => {
  const messageTyped = useContext(currentMessageContext);
  const handleCurrentMessageChange =(event)=>{
    messageTyped.updatecurrentMessage(event.target.value);
  }
  return (
    <div>
      
        <form className="chatbox" >
          <input type="text" placeholder='Type here'  onChange={handleCurrentMessageChange}/>
          <button type='submit' className="send-icon" onSubmit={onSendingMessage}>
            <img src={sendIcon} alt="Send" />
          </button>
        </form>
      
    </div>
  );
}

export default Chatbox;
