  //       query: { id },
  //       transports: ['websocket'],
  //       reconnectionAttempts: 3,
  //       timeout: 10000,
  //       auth: { token: data.token }
  //     });
  //     setSocket(newSocket);
  //   })
  //   .catch(error => {
  //     console.error('Error connecting to socket:', error);
  //   });
  
  //   return () => {
  //     if (socket) {
  //       socket.disconnect();
  //     }
  //   };
  // }, [id,socket]);
  

  return (
    <SocketContext.Provider value={socket}>
      {children}
    </SocketContext.Provider>
  )
}
