import React, { useState } from 'react';
import sendIcon from '../img/send.png';
import { useContext , useEffect} from 'react';
// import userContext from '../context/message/userContext';
import userContext from "../context/users/userContext";
import { useSocket } from '../context/SocketProvider';
const Chatbox = (props) => {
  // const message = useContext(currentMessageContext);
  const User=useContext(userContext);
  const socket = useSocket();
  const [messageTyped,setmessageTyped]=useState("");
  
  const handleOnChange=(event)=>{
    setmessageTyped(event.target.value);
  }
  const [recipients,setrecipients] =useState([User.currentUser.id]);
  useEffect(()=>{
    setrecipients([User.currentUser.id]);
  },[User.currentUser.id])
  // console.log(User.currentUser.id); // Log the updated value of recipients
  // console.log(recipients); // Log the updated value of recipients
  const myId=props.myId;
  useEffect(() => {
    if (socket) {
      socket.on('receive-message', ({ recipients, sender, text }) => {
        console.log(text,sender);
        User.addMessageToContact({'text':text,id:[sender]});
      });

      return () => {
        socket.off('receive-message');
      };
    }
  }, []);
  const onSendingMessage = async (event) => {
    // await User.updateMessage(messageTyped);
    await User.addMessageToContact({text:messageTyped,id:recipients});
    await socket.emit("send-message", { recipients, text: messageTyped });
  
    setmessageTyped("");
    event.preventDefault();
  };
  
  // useEffect(() => {
  //   if(User.Message.length>1){
  //     console.log(User.Message);
  //   }
  // }, [User.Message]);
  return (
    <div>
      
        <form className="chatbox" >
          <input type="text" placeholder='Type here' value={messageTyped} onChange={handleOnChange}/>
          <button type='submit' className="send-icon" onClick={onSendingMessage}>
            <img src={sendIcon} alt="Send" />
          </button>
        </form>
      
    </div>
  );
}

export default Chatbox;
