import React, { useContext, useEffect } from 'react'
import Contacts from './Contacts'
import Addgroup from './Addgroup'
import userContext from '../context/users/userContext'
import axios from 'axios'
const Leftpane = (props) => {
  const User = useContext(userContext);
  useEffect(() => {
    const fetchContactList = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/user/contacts/accesscontacts?myId=${props.myId}`);
        const myContactList = response.data; // Assuming contact list is directly returned in response.data
        console.log("hello");
        console.log(myContactList);
        const mappedContacts = myContactList.map((contact) => {
          return {
            name: contact.myId,
            id: contact.recipients[0],
            messages: contact.messages
          };
        });
        User.setmyContacts(mappedContacts);
        // Update User context or state with the contact list
      } catch (error) {
        console.log(error.message);
      }
    };

    fetchContactList();
  }, [props.myId]);
  return (
  
      <div className="leftpane">
        <div className="head_con">
        <h4>Contacts</h4>
        </div>
        <div className="contact-list">
          {User.myContacts.map((contact)=>{
            return(
              <Contacts username={contact.name} id={contact.id} key={contact.id}/> 
            )
          })}
        </div>
        {/* <Contacts username="User1" id="1"/> 
        <Contacts username="User2" id="2"/> 
        <Contacts username="User3" id="3"/> 
        <Contacts username="User4" id="4"/> 
        <Contacts username="User5" id="5"/>  */}
        <Addgroup />

      </div>
   
  )
}

export default Leftpane
