import React, { useContext } from 'react'
import userContext from '../context/users/userContext';
import axios from 'axios';

const Addgroup = (props) => {
  const User = useContext(userContext);

  const handleOnClick =async()=>{
    const groupName = window.prompt('Enter group name:');
    const groupId = window.prompt('Enter group ID:');

    if(!groupId || !groupName){
      window.alert("Fill All Entries");
    }

    try{
      const config ={
        headers: {
          "Content-type":"application/json",
        }
      };
      const {data} = await axios.put("http://localhost:5000/api/user/contacts/addContact",{"myId":props.myId,recipients:groupId,contactName:groupName},config);
      // console.log(data);

      const contacts =await axios.get(`http://localhost:5000/api/user/contacts/accessContacts?myId=${props.myId}`);
      await User.updatemyContacts(contacts);
      console.log("addgroup::",contacts);
    }catch(error){
      console.log(error.message);
    }
    // if (groupName && groupId) {
    //   const groupData = { name: groupName, id: groupId };
    //   User.updatemyContacts(groupData);
    // }
  }
  return (
    <div>
        <div className="add-grp-btn">
            <button className="add-button" onClick={handleOnClick}> + </button>
        </div>
    </div>
  )
}

export default Addgroup
