import {useState} from "react";
import MessageContext from "./currentMessageContext"; 


const MessageState = (props)=>{
    const [Message,setMessage] = useState([{
        "text": "",
    }])
    const updateMessage=({newMessage,sender})=>{
        setMessage([...Message,{
            "text":newMessage,
        }])
    }
    return (
        <currentMessageContext value={{Message,updateMessage}}>
            {props.children};
        </currentMessageContext>
    )
}

export default currentMessageState;
